INSERT INTO films(name, description, year, genre_id, minimal_age, duration_in_minutes, file_id) VALUES
  ('Багровая отмель', '1940-е. Команда водолазов получает заказ на подъём затонувшей машины с грузом золота. Однако попытка выполнить работу оборачивается настоящим кошмаром, когда они подвергаются нападению агрессивной акулы, а в охоту за кладом включаются криминальные силы.', 2025, 5, 18, 85, 1),
  ('Балерина', 'Опытная наёмница и по совместительству профессиональная балерина охотится на убийц своей семьи.', 2025, 1, 18, 125, 2),
  ('Батя 2. Дед', 'Во время развода с женой Макс вспоминает детство, когда родители, которые тоже собирались разводиться, отправили его на лето к деду-фронтовику в деревню. Тогда строгий дед стал настоящим другом и учителем для мальчика, а также свидетелем и участником его многочисленных проделок.', 2025, 3, 18, 88, 3),
  ('Черные священники', 'Святой отец Ким ищет помощника для проведения обряда изгнания демона, вселившегося в тело школьницы. Выбор падает на диакона Чхве. Однако ему не даёт покоя то, что много лет назад случилось с его сестрой. Второй экзорцист обязан посмотреть в лицо своим страхам, чтобы помочь священнику спасти одержимую, и самому не попасться на удочку демона.', 2015, 5, 18, 103, 4),
  ('Группа крови', 'Ноябрь 1943 года. Фашисты привозят новую партию советских детей-сирот в Вырицу, где в бывшем пионерлагере развёрнут так называемый «детский приют». Условия здесь невыносимые: холод, голод, принудительный труд, карцер за неповиновение, за попытку побега — расстрел. Но самое страшное: детей здесь используют как доноров крови для раненых немцев. Несмотря на весь ужас и немыслимость происходящего, сплотившись, юные герои находят в себе силы и смелость сопротивляться и решаются на побег.', 2025, 2, 12, 98, 5),
  ('Кракен', 'Российский ракетный подводный крейсер специального назначения бесследно исчезает во время секретной миссии в Гренландском море. На его поиски отправляется экипаж под командованием Виктора Воронина, чей старший брат командовал пропавшей субмариной.', 2025, 2, 12, 134, 6),
  ('Маракуда', 'Сын вождя Маракуда должен стать настоящим воином, но есть одна проблема — он совершенно не умеет охотиться. Отправившись в путешествие в таинственный лес, он встречает священную птицу Тинк, которая дарует ему удивительную способность понимать язык животных и птиц. Вместе с Тинком ему предстоит пережить захватывающие приключения, подружиться с самыми необычными существами и разгадать загадки леса. Но главное — научиться слышать не только мир вокруг, но и самого себя.', 2025, 4, 6, 89, 7),
  ('Призрак в доспехах 2: Невинность', 'Близкое будущее, 2032 год. Техника достигла невиданного прогресса, люди делят мир с киборгами и роботами. Бато — детектив отдела полиции по борьбе с терроризмом. Он — живой киборг, оснащённый искусственными руками, ногами и прочими частями тела. Остался лишь его прежний мозг да память о ней, о Мотоко Кусанаги.', 2004, 4, 18, 100, 8),
  ('Туда', 'Вадим привык ездить и жить по навигатору. Жизнь Веры — одно большое приключение. Двум противоположностям предстоит проехать 1500 километров на обычном городском такси, увидеть пять живописных городов и испытать миллион эмоций на пути друг к другу.', 2025, 3, 16, 104, 9),
  ('В списках не значился', '21 июня 1941 года. Молодой лейтенант Коля Плужников, получив назначение на постоянное место службы, приезжает в Брест. Переполненные залы ожидания вокзала и толпа увешанных багажом людей не настораживают охваченного радостными надеждами юношу. Коля спешит к месту расположения своей части — в Брестскую крепость… ', 2025, 1, 12, 120, 10);
